<template>
    <div class="auth-page">
        <div class="auth-tabs">
            <button class="active">{{ localeStore.t('login') }}</button>
            <NuxtLink to="/register" class="tab-link">{{ localeStore.t('register') }}</NuxtLink>
        </div>

        <form class="auth-form" @submit.prevent="handleLogin">
            <div class="input-group">
                <input type="text" :placeholder="localeStore.t('emailOrPhone')" v-model="emailOrPhone" @input="validateForm">
            </div>

            <div class="input-group">
                <input type="password" :placeholder="localeStore.t('password')" v-model="password" @input="validateForm">
            </div>

            <div class="agreement">
                <label>
                    <input type="checkbox" v-model="isAgreementChecked" @change="validateForm">
                    <span>{{ localeStore.t('agreementText') }}</span>
                </label>
            </div>

            <button type="submit" class="submit-btn" :disabled="!isFormValid">
                {{ localeStore.t('login') }}
            </button>
        </form>

        <div class="auth-footer">
            <div class="links">
                <a href="#">{{ localeStore.t('forgotPassword') }}</a>
                <a href="#">{{ localeStore.t('phoneLogin') }}</a>
            </div>

            <div class="divider">
                <span>{{ localeStore.t('otherLoginMethods') }}</span>
            </div>

