        <div v-if="showAgreementModal" class="modal-overlay">
            <div class="modal-content">
                <h3>{{ localeStore.t('privacyPolicyConfirmation') }}</h3>
                <p>{{ localeStore.t('privacyPolicyText') }}</p>
                <div class="modal-actions">
                    <button @click="cancelAgreement">{{ localeStore.t('cancel') }}</button>
                    <button @click="confirmAgreement" class="confirm-btn">{{ localeStore.t('agreeAndContinue') }}</button>
                </div>
            </div>
        </div>
    </div>
</template>

<script setup>
import { ref } from 'vue';
import { useRouter } from 'vue-router';
import { useLocaleStore } from '~/stores/locale'; // 导入语言存储

definePageMeta({
    layout: "auth"
});

const router = useRouter();
const localeStore = useLocaleStore(); // 使用语言存储

// 表单数据
const emailOrPhone = ref('');
const password = ref('');
const isAgreementChecked = ref(false);
const isFormValid = ref(false);
const showAgreementModal = ref(false);

// 表单验证
const validateForm = () => {
    // 检查邮箱/手机号和密码是否填写
    const isInputValid = emailOrPhone.value.trim() !== '' && password.value.trim() !== '';

    // 更新表单有效性状态
    isFormValid.value = isInputValid;
};

// 处理登录
const handleLogin = () => {
    if (!isAgreementChecked.value) {
        // 显示隐私协议确认对话框
        showAgreementModal.value = true;
        return;
    }

    // 所有验证通过，执行登录逻辑
    performLogin();
};

// 确认同意隐私协议
const confirmAgreement = () => {
    isAgreementChecked.value = true;
    showAgreementModal.value = false;
    performLogin();
};

// 取消同意隐私协议
const cancelAgreement = () => {
    showAgreementModal.value = false;
};

// 执行登录逻辑
const performLogin = () => {
    console.log('执行登录:', {
        emailOrPhone: emailOrPhone.value,
        password: password.value
    });

    // 登录成功后跳转到首页
    router.push('/');
};
</script>

